from flask import Flask, render_template, request, redirect
from flask_bootstrap import Bootstrap
from flask_wtf import FlaskForm 
from wtforms import StringField, IntegerField, SubmitField
from wtforms.validators import DataRequired
from flask_sqlalchemy import SQLAlchemy
from flask_migrate import Migrate, migrate

#Flask Configuration
app = Flask(__name__)
app.debug = True
bootstrap = Bootstrap(app)

#Database Configuration
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///site.db'
db = SQLAlchemy(app)
app.app_context().push()

# Settings for migrations
migrate = Migrate(app, db)

class activityDB(db.Model):
    title = db.Column(db.String(50), unique=True, nullable=False, primary_key = True)
    type = db.Column(db.String(10), unique=False, nullable=False)
    image_url = db.Column(db.String(), nullable=True)
    author = db.Column(db.String(40), unique=False, nullable=False)
    note = db.Column(db.String(300), unique=False, nullable=False)
    date = db.Column(db.String(10), unique=False, nullable=False)
    
    def __repr__(self):
        return f"Title: {self.first_name}, Type: {self.type}, Poster: {self.author}, Date: {self.date}"
    
    def __eq__(self, value):
        return self.date[:4] == value.date[:4]
    
    def __lt__(self, other):
        return self.date[:4] < other.date[:4]
        
    def __gt__(self, other):
        return self.date[:4] > other.date[:4]

#Here is the activity object class
class activity():
    def __init__(self, type, title, image_url, author, note, date):
        self.type = type
        self.title = title
        self.image_url = image_url
        self.author = author
        self.note = note
        self.date = date
      
    #All these are comparison operators for the sort function.
    #They judge if something is equal, earlier, or later based on date, specifically the year
    #While more complex things could be made to determine sorting base on the complete data this is a simple prototype to see if you can sort activity objects in this way at all.
    def __eq__(self, value):
        return self.date[:4] == value.date[:4]
    
    def __lt__(self, other):
        return self.date[:4] < other.date[:4]
        
    def __gt__(self, other):
        return self.date[:4] > other.date[:4]

class activityForm(FlaskForm):
    title = StringField(
        'Title', 
        validators=[DataRequired()]
    )
    type = StringField(
        'Type(Book, Game, Movie)', 
        validators=[DataRequired()]
    )
    author = StringField(
        'Author', 
        validators=[DataRequired()]
    )
    image_url = StringField(
        'Image Adress', 
        validators=[DataRequired()]
    )
    note = StringField(
        'Note/Review', 
        validators=[DataRequired()]
    )
    date = StringField(
        'Date(y/m/d)', 
        validators=[DataRequired()]
    )
    
    def __eq__(self, value):
        return self.date == value.date

def store_song(type, title, image_url, author, note, date):
    #Line below previously used in debuggin now unneccesary but might be needed again in the future.
    # print("----- ARRIVED IN STORE SONG _________")
    
    #Takes the data made in the insert page's forms turns it into an activity object then appends it to the activities list that holds all the items to be displayed.
    global activities
    activities.append(
        #type is converted to lowercase since all of the tags are in lower case, but consumers might put it in as uppercase due to familiarity
        activity(str(type).lower(), str(title), str(image_url), str(author), str(note), str(date))
    )
    #Sort the activities list here.
    activities.sort(reverse=True)
    
