@app.route('/')
def home_page():
    return render_template('index.html')

@app.route('/insert', methods=['GET', 'POST'])
def insert_page():
    title = request.form.get("title")
    type = request.form.get("type")
    date = request.form.get("date")
    image_url = request.form.get("image_url")
    note = request.form.get("note")
    author = request.form.get("author")
    
    
    if title != '' and type != '' and date != '' and image_url != '' and note != '' and author != '':
        p = activityDB(title = title, type = type, date = date, image_url = image_url, note = note, author = author)
        db.session.add(p)
        db.session.commit()
        return redirect('/')
    return render_template('insert.html', )

@app.route('/delete/<string:title>')
def erase(title):
    # Deletes the data on the basis of unique title and redirects to home page
    data = activityDB.query.get(title)
    db.session.delete(data)
    db.session.commit()
    return redirect('/')

@app.route('/detail/<int:index>')
def inspect(index):
    return render_template('detail.html', act = activities[index])

@app.route('/page/<string:type>')
def display_page(type):
    return render_template('page.html', type = type, act = activities)

if __name__ == '__main__':
    app.run()

